      <script>
         $(document).ready(function() {
            $(window).on('scroll', function() {
               var counters = $('.counter');
               
               // Fonction pour vérifier si l'élément est dans la fenêtre visible
               function isElementInViewport(el) {
                     var rect = el[0].getBoundingClientRect();
                     return (
                        rect.top >= 0 &&
                        rect.left >= 0 &&
                        rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
                        rect.right <= (window.innerWidth || document.documentElement.clientWidth)
                     );
               }

               counters.each(function() {
                  var counter = $(this);
                  if (isElementInViewport(counter) && !counter.data('counted')) {
                     counter.data('counted', true);
                     var target = parseInt(counter.attr('data-target'));
                     var duration = 2000; // Durée de l'animation en millisecondes
                     var count = 0;
                     var step = target / duration * 10;
                     var interval = setInterval(function() {
                        count += step;
                        counter.text(Math.floor(count));
                        if (count >= target) {
                              clearInterval(interval);
                              counter.text(target);
                        }
                     }, 10);
                  }
               });
            });
            // Appel de la fonction au chargement de la page pour initialiser les compteurs visibles
            $(window).trigger('scroll');
         });
      </script>

